CREATE TABLE offices (office_code INTEGER AUTO_INCREMENT NOT NULL, address VARCHAR(255), city VARCHAR(255), country VARCHAR(255), phone VARCHAR(255), postal_code INTEGER, state VARCHAR(255), territory VARCHAR(255), PRIMARY KEY (office_code))
CREATE TABLE productilnes (product_line INTEGER AUTO_INCREMENT NOT NULL, html_description VARCHAR(255), image VARCHAR(255), text_description VARCHAR(255), code INTEGER, PRIMARY KEY (product_line))
