CREATE TABLE orders (order_ INTEGER NOT NULL, comment VARCHAR(255), customer_number INTEGER, date DATE, required_date DATE, shipped_date DATE, status VARCHAR(255), PRIMARY KEY (order_))
CREATE TABLE products (code INTEGER AUTO_INCREMENT NOT NULL, buy_price FLOAT, description VARCHAR(255), line INTEGER, msrp INTEGER, name VARCHAR(255), quantity_in_stock INTEGER, scale INTEGER, vendor INTEGER, PRIMARY KEY (code))
CREATE TABLE order_details (details_id INTEGER AUTO_INCREMENT NOT NULL, order_line_number INTEGER, price_each INTEGER, quantity_ordered INTEGER, code INTEGER, order_number INTEGER, PRIMARY KEY (details_id))
CREATE TABLE customers (customer_number INTEGER AUTO_INCREMENT NOT NULL, address VARCHAR(255), city VARCHAR(255), contact VARCHAR(255), country VARCHAR(255), credit_limit FLOAT, name VARCHAR(255), phone VARCHAR(255), postal_code INTEGER, sales_rep_employee_number INTEGER, state VARCHAR(255), PRIMARY KEY (customer_number))
